create table Pacientes (idPaciente varchar(20) not null, passPaciente varchar(16) not null, nombre varchar(20) not null, telefono_paciente varchar(10) not null, poliza_seg varchar(15) not null, correoPaciente varchar(30) not null, fechaNacto date not NULL, PRIMARY KEY (idPaciente));
create table Citas (idCita int NOT NULL AUTO_INCREMENT, policlinica varchar(50) not null, especialidad varchar(30) not null, fechaCita date not null, horaCita time not null, idPaciente varchar(20) not null, primary KEY (idCita), CONSTRAINT FK_Citas_idPaciente FOREIGN KEY (idPaciente) REFERENCES pacientes(idPaciente));
create table Medico (idMedico varchar(20) not null, passMedico varchar(16) not null, nombreMedico varchar(20) not null, telefonoMedico varchar(10) not null, Cargo varchar(10) not null, correoMedico varchar(30) not NULL, PRIMARY KEY(idMedico));
create table Admin (emailAdmin varchar(30) not null, passAdmin varchar(16) not null, nombreAdmin varchar(30) not NULL);